extern "C" int LLVMFuzzerTestOneInput(const uint8_t *data, size_t size) {
  FuzzedDataProvider fdp(data, size);

  double hdpi = fdp.ConsumeFloatingPoint<double>();
  double vdpi = fdp.ConsumeFloatingPoint<double>();
  bool useMediaBox = fdp.ConsumeBool();
  bool crop = fdp.ConsumeBool();
  bool printing = fdp.ConsumeBool();
  std::vector<char> payload = fdp.ConsumeRemainingBytes<char>();

  Object xpdf_obj;
  xpdf_obj.initNull();
  BaseStream *stream = new MemStream(payload.data(), 0, payload.size(), &xpdf_obj);

  Object info, xfa;
  Object *acroForm;
  SplashColor paperColor = {0xff, 0xff, 0xff};
  SplashOutputDev *splashOut = NULL;
  GfxState *state = NULL;

  globalParams = new GlobalParams(NULL);
  globalParams->setErrQuiet(1);
  globalParams->setupBaseFonts(NULL);
  char yes[] = "yes";
  globalParams->setEnableFreeType(yes); // Yes, it's a string and not a bool.
  globalParams->setErrQuiet(1);

  PDFDoc *doc = NULL;
  try {
    doc = new PDFDoc(stream);
    if (doc->isOk() == gTrue) {
      doc->getNumPages();
      doc->getOutline();
      doc->getStructTreeRoot();
      doc->getXRef();

      // Create an instance of SplashOutputDev
      splashOut = new SplashOutputDev(splashModeRGB8, 4, gFalse, paperColor);
      splashOut->startDoc(doc->getXRef());

      // Removed call to doUpdateFont
    }
  } catch (...) {
  }
  delete doc;
  delete globalParams;
  delete splashOut;
  return 0;
}

#include <stddef.h>
#include <stdint.h>
#include <poppler/GfxState.h>
#include <poppler/SplashOutputDev.h>

extern "C" int LLVMFuzzerTestOneInput(const uint8_t *data, size_t size) {
  if (size < 1) {
    return 0;
  }

  // Initialize GfxState object
  GfxState *state = new GfxState(72.0, 72.0, NULL, 0, gTrue, gFalse);

  // Initialize SplashOutputDev object
  SplashOutputDev *splashOutputDev = new SplashOutputDev(splashModeRGB8, 4, gFalse, NULL, gFalse);

  // Call the target function
  splashOutputDev->doUpdateFont(state);

  // Clean up
  delete state;
  delete splashOutputDev;

  return 0;
}
