#define CONFIG_SIZE 4096

extern "C" int LLVMFuzzerTestOneInput(const uint8_t *data, size_t size) {
  FuzzedDataProvider fdp(data, size);

  std::string name = fdp.ConsumeRandomLengthString();

  Object obj;
  obj.initNull();

  MemStream *str = new MemStream(name.data(), 0, size, &obj);
  PDFDoc *doc = new PDFDoc(str);

  globalParams = new GlobalParams((char *)data);
  globalParams->setupBaseFonts(NULL);

  if (doc->isOk()) {
    int rotate = fdp.ConsumeIntegral<int>();
    PDFRectangle paperBox;
    GfxState* gfxState = new GfxState(72, 72, &paperBox, rotate, true);
    SplashColor splashColorWhite;
    memset(splashColorWhite, 0xff, sizeof(splashColorWhite));
    SplashOutputDev *outputDev = new SplashOutputDev(splashModeRGB8, 1, 1, splashColorWhite, 0, 0, 0);
    outputDev->startDoc(doc->getXRef());
    delete outputDev;
    delete gfxState;
  } 

  delete doc;
  delete globalParams;

  return 0;
}

